mapper.insert:
INSERT INTO CHATMEMBER (ROOM_NO, MEMBER_NO)
SELECT #{roomNo}, #{memberNo}
FROM DUAL
WHERE NOT EXISTS (
    SELECT 1 FROM CHATMEMBER
    WHERE ROOM_NO = #{roomNo}
    AND MEMBER_NO = #{memberNo}
)

mapper.delete:
DELETE FROM CHATMEMBER
WHERE ROOM_NO = #{roomNo}
AND MEMBER_NO = #{memberNo}
